<?php

/**
 * The admin-specific functionality of the plugin.
 *
 * @link       https://www.wplauncher.com
 * @since      1.0.0
 *
 * @package    Chatlab_WooCommerce
 * @subpackage Chatlab_WooCommerce/admin
 */

/**
 * The admin-specific functionality of the plugin.
 *
 * Defines the plugin name, version, and two examples hooks for how to
 * enqueue the admin-specific stylesheet and JavaScript.
 *
 * @package    Chatlab_WooCommerce
 * @subpackage Chatlab_WooCommerce/admin
 */
class ChatlabSettings_Page_Admin {

	/**
	 * The ID of this plugin.
	 *
	 * @since    1.0.0
	 * @access   private
	 * @var      string    $plugin_name    The ID of this plugin.
	 */
	private $plugin_name;

	/**
	 * The version of this plugin.
	 *
	 * @since    1.0.0
	 * @access   private
	 * @var      string    $version    The current version of this plugin.
	 */
	private $version;

	/**
	 * Initialize the class and set its properties.
	 *
	 * @since    1.0.0
	 * @param      string    $plugin_name       The name of this plugin.
	 * @param      string    $version    The version of this plugin.
	 */
	public function __construct( $plugin_name, $version ) {

		$this->plugin_name = $plugin_name;
		$this->version = $version;
		add_action('admin_menu', array( $this, 'addPluginAdminMenu' ), 9);   
		add_action('admin_init', array( $this, 'registerAndBuildFields' )); 
		add_action('admin_notices', array( $this, 'chatlab_not_configured_admin_notice' ));

	}

	/**
	 * Register the stylesheets for the admin area.
	 *
	 * @since    1.0.0
	 */
	public function enqueue_styles() {

		/**
		 * This function is provided for demonstration purposes only.
		 *
		 * An instance of this class should be passed to the run() function
		 * defined in Settings_Page_Loader as all of the hooks are defined
		 * in that particular class.
		 *
		 * The Settings_Page_Loader will then create the relationship
		 * between the defined hooks and the functions defined in this
		 * class.
		 */

		wp_enqueue_style( $this->plugin_name, plugin_dir_url( __FILE__ ) . 'css/settings-page-admin.css', array(), $this->version, 'all' );

	}

	const ALLOWED_HTML = array(
		'a' => array(
			'href' => array()
		),
		'br'     => array(),
		'em'     => array(),
		'strong' => array()
	);
		/**
	 * Register the JavaScript for the admin area.
	 *
	 * @since    1.0.0
	 */
	public function enqueue_scripts() {

		/**
		 * This function is provided for demonstration purposes only.
		 *
		 * An instance of this class should be passed to the run() function
		 * defined in Settings_Page_Loader as all of the hooks are defined
		 * in that particular class.
		 *
		 * The Settings_Page_Loader will then create the relationship
		 * between the defined hooks and the functions defined in this
		 * class.
		 */

		wp_enqueue_script( $this->plugin_name, plugin_dir_url( __FILE__ ) . 'js/settings-page-admin.js', array( 'jquery' ), $this->version, false );

	}



	public function addPluginAdminMenu() {
		//add_menu_page( $page_title, $menu_title, $capability, $menu_slug, $function, $icon_url, $position );
	$icon = "data:image/svg+xml;base64,";
		add_menu_page(  $this->plugin_name, 'ChatLab', 'administrator', $this->plugin_name, array( $this, 'displayPluginAdminDashboard' ), $icon, 26 );

		//add_submenu_page( '$parent_slug, $page_title, $menu_title, $capability, $menu_slug, $function );
		add_submenu_page( $this->plugin_name, 'ChatLab Settings', 'Settings', 'administrator', $this->plugin_name.'-settings', array( $this, 'displayPluginAdminSettings' ));
	}
	public function displayPluginAdminDashboard() {
		require_once 'partials/'.$this->plugin_name.'-admin-display.php';
  }
	public function displayPluginAdminSettings() {
		// set this var to be used in the settings-display view
		if(isset($_GET['error_message'])){
				add_action('admin_notices', array($this,'settingsPageSettingsMessages'));
				do_action( 'admin_notices', esc_html(sanitize_text_field($_GET['error_message'])) );
		}
		require_once 'partials/'.$this->plugin_name.'-admin-settings-display.php';
	}
	public function settingsPageSettingsMessages($error_message){
		switch ($error_message) {
				case '1':
						$message = __( 'There was an error adding this setting. Please try again. If this persists, shoot us an email.','chatlab-ai-chatbot-for-your-website-gpt-powered-customer-sales-assistant' );
						$err_code = esc_attr( 'chatlab_apikey' );
						$setting_field = 'chatlab_apikey';
						break;
		}
		$type = 'error';
		add_settings_error(
					$setting_field,
					$err_code,
					$message,
					$type
			);
	}
	public function registerAndBuildFields() {
			/**
		 * First, we add_settings_section. This is necessary since all future settings must belong to one.
		 * Second, add_settings_field
		 * Third, register_setting
		 */     
		add_settings_section(
			// ID used to identify this section and with which to register options
			'chatlab_general_section',
			// Title to be displayed on the administration page
			'',  
			// Callback used to render the description of the section
				array( $this, 'settings_page_display_general_account' ),    
			// Page on which to add this section of options
			'chatlabsettings_page_chatlab_general_settings'
		);
		unset($args);
		$args = array (
							'type'      => 'input',
							'subtype'   => 'text',
							'id'    => 'chatlab_apikey',
							'name'      => 'chatlab_apikey',
							'required' => 'true',
							'get_options_list' => '',
							'value_type'=>'normal',
							'wp_data' => 'option'
					);
		add_settings_field(
			'chatlab_apikey',
			'Chatbot Bot ID',
			array( $this, 'chatlab_render_settings_field' ),
			'chatlabsettings_page_chatlab_general_settings',
			'chatlab_general_section',
			$args
		);


		register_setting(
						'chatlabsettings_page_chatlab_general_settings',
						'chatlab_apikey'
						);

	}
	function chatlab_not_configured_admin_notice() {
		$screen = get_current_screen();

		if ( $screen->id !== 'toplevel_page_chatlab-settings-page') return;


		// Check if the setting has a value
		$setting_value = get_option('chatlab_apikey');
		if (!$setting_value) {

			?>
			<div class="wrap">
			<div id="icon-themes" class="icon32"></div>
			<h2>Chatbot integration with WooCommerce</h2>

				<div class="notice notice-warning is-dismissible">
					<p><?php
						$str = 'Please configure API-KEY for your chatlab bot. Got to plugin settings page: <a href="' . esc_url(admin_url('admin.php?page=chatlab-settings-page-settings')) . '">Settings</a>.';
						echo wp_kses($str, self::ALLOWED_HTML);?></p>
				</div>
			</div>
			<?php
		}
	}

	public function settings_page_display_general_account() {
		$str = '<p>To integrate your chatbot, please provide the Chatbot ID for the bot you\'ve created in the ChatLab administration panel. You can find your Chatbot ID after creating a chatbot on <a href="https://app.chatlab.com" target="_blank">ChatLab.com</a>. For step-by-step instructions on how to create a chatbot, please visit this  <a href="https://help.chatlab.com/creating-a-chatbot" target="_blank">guide</a>.</p>';
		echo wp_kses($str, self::ALLOWED_HTML);
	} 
	public function chatlab_render_settings_field($args) {
			/* EXAMPLE INPUT
								'type'      => 'input',
								'subtype'   => '',
								'id'    => $this->plugin_name.'_example_setting',
								'name'      => $this->plugin_name.'_example_setting',
								'required' => 'required="required"',
								'get_option_list' => "",
									'value_type' = serialized OR normal,
			'wp_data'=>(option or post_meta),
			'post_id' =>
			*/     
		if($args['wp_data'] == 'option'){
			$wp_data_value = get_option($args['name']);
		} elseif($args['wp_data'] == 'post_meta'){
			$wp_data_value = get_post_meta($args['post_id'], $args['name'], true );
		}

		$allowed_html = array(
			'div' => array(
				'class' => array(),
			),
			'span' => array(
				'class' => array(),
			),
			'input' => array(
				'type' => array(),
				'id' => array(),
				'name' => array(),
				'size' => array(),
				'value' => array(),
				'required' => array(),
			),
		);


		switch ($args['type']) {

			case 'input':
					$value = ($args['value_type'] == 'serialized') ? serialize($wp_data_value) : $wp_data_value;
					if($args['subtype'] != 'checkbox'){
							$prependStart = (isset($args['prepend_value'])) ? '<div class="input-prepend"> <span class="add-on">'.$args['prepend_value'].'</span>' : '';
							$prependEnd = (isset($args['prepend_value'])) ? '</div>' : '';
							$step = (isset($args['step'])) ? 'step="'.$args['step'].'"' : '';
							$min = (isset($args['min'])) ? 'min="'.$args['min'].'"' : '';
							$max = (isset($args['max'])) ? 'max="'.$args['max'].'"' : '';
							if(isset($args['disabled'])){
								$input= $prependStart.'<input type="'.$args['subtype'].'" id="'.$args['id'].'_disabled" '.$step.' '.$max.' '.$min.' name="'.$args['name'].'_disabled" size="40" disabled value="' . esc_attr($value) . '" /><input type="hidden" id="'.$args['id'].'" '.$step.' '.$max.' '.$min.' name="'.$args['name'].'" size="40" value="' . esc_attr($value) . '" />'.$prependEnd;
								echo wp_kses($input, $allowed_html);

							} else {
								$input = $prependStart.'<input type="'.$args['subtype'].'" id="'.$args['id'].'" "'.$args['required'].'" '.$step.' '.$max.' '.$min.' name="'.$args['name'].'" size="40" value="' . esc_attr($value) . '" />'.$prependEnd;
								echo wp_kses($input, $allowed_html);
							}

					} else {
							$checked = ($value) ? 'checked' : '';

							$input =  '<input type="'.$args['subtype'].'" id="'.$args['id'].'" "'.$args['required'].'" name="'.$args['name'].'" size="40" value="1" '.$checked.' />';
							echo wp_kses($input, $allowed_html);
					}
					break;
			default:
					# code...
					break;
		}
	}
}
